angular.module("mainAdminApp").service("adminServices",function(t){this.backendUrl="/admin",this.login=function(d){return t({method:"POST",url:this.backendUrl+"/login",data:d})},this.addProduct=function(d){return t({method:"POST",url:this.backendUrl+"/addProduct",data:{productDetails:d}})},this.updateProduct=function(d){return t({method:"POST",url:this.backendUrl+"/updateProduct",data:{productDetails:d}})},this.fetchProducts=function(d){return t({method:"GET",url:this.backendUrl+"/fetchProducts?pageNumber="+d})},this.fetchProductDetails=function(d){return t({method:"GET",url:this.backendUrl+"/fetchProductDetails?id="+d})}});