angular.module("mainAdminApp").controller("parentAdminCtrl",["$scope","$rootScope","$state",function(o,n,t){o.logout=function(){n.adminLoggedIn=!1,localStorage.removeItem("adminLoggedIn"),t.go("homePage")},o.addProduct=function(){t.go("productDetails",{src:"newProduct"})},o.showAllProducts=function(){t.go("products",{pageNumber:0})},o.init=function(){"true"===localStorage.getItem("adminLoggedIn")?n.adminLoggedIn=!0:n.adminLoggedIn=!1},o.init()}]);